        var newurl = this.getImageURL('/api/next').then(newurl => {
            console.log(newurl);
            this.setState({ src: newurl })
        });

    }

    render() {
        return (
            <img class="responsive" onClick={this.nextPhoto.bind(this)} src={this.state.src}></img>
        );

    }
} export default Image;
