import React from 'react';
import './Slideshow.css';
import { TransitionGroup, SwitchTransition, CSSTransition } from 'react-transition-group';

function Slideshow() {
    return (
        <div class="slideshow-image">
            <Image id='image' name="DSC00533.jpg"></Image>
        </div>
    );
}

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            src: "./DSC00533.jpg"
        };
    }

    nextPhoto() {

        var outside;

        fetch('/api/next')
            .then(response => response.blob())
            .then(images => {
                // Create a local URL for that image and print it 
                outside = URL.createObjectURL(images)
                this.setState({ src: outside });
            })
    }

    render() {
        return (
            // <SwitchTransition>
            //     <CSSTransition>
            //         transitionName="fade"
            //         transitionAppear={true}
            //         transitionAppearTimeout={500}
            //         transitionEnter={false}
            //         transitionLeave={false}>
                    <img class="responsive" onClick={this.nextPhoto.bind(this)} src={this.state.src}></img>
            //     </CSSTransition>
            // </SwitchTransition>
        );

    }
}

export default Slideshow;
