import React from 'react';
import './Slideshow.css';

class Slideshow extends React.Component() {
    constructor(props) {
        super(props);
        this.state = {
            firstImage: null
        }

        fetch('/api/first-image')
            .then(response => response.blob())
            .then(images => {
                this.setState({ firstImage: URL.createObjectURL(images) });
            })
    }

    render() {
        return(
            <div class="slideshow-image">
                <Image id='image' name={this.state.firstImage}></Image>
            </div>
        )
    }
}

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            src: props.name
        };
    }

    nextPhoto() {

        var outside;

        fetch('/api/next')
            .then(response => response.blob())
            .then(images => {
                // Create a local URL for that image and print it 
                outside = URL.createObjectURL(images)
                this.setState({ src: outside });
            })
    }

    render() {
        return (
            <img
                class="responsive"
                onClick={this.nextPhoto.bind(this)}
                src={this.state.src}>
            </img>
        );

    }
}

export default Slideshow;
