import React from 'react';
import './Image.css';

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            src: props.name
        };
        console.log(this.state);
    }

    getImageURL(url) {
        var imageurl;
        fetch(url)
            .then(response => response.blob())
            .then(image => {
                // Create a local URL for that image and print it 
                imageurl = URL.createObjectURL(image)
            })
        console.log("imageurl" + imageurl);
        return imageurl;
    }

    nextPhoto() {
        var next = this.getImageURL('/api/next');
        console.log("next" + next);
        this.setState({ src: next });
    }

    render() {
        return (
            <img
                class="responsive"
                onClick={this.nextPhoto.bind(this)}
                src={this.state.src}>
            </img>
        );

    }
}
export default Image;
